});

// Route to get sponsorCid 
app.get('/api/sponsor/:usernameCid', async (req, res) => {
  const { usernameCid } = req.params;

  try {
    const [consultants] = await db.query('SELECT * FROM consultants WHERE username_cid = ?', [usernameCid]);

