});

app.get('/api/sponsor-detail/:sponsorCid', authenticate, async (req, res) => {
  try {
    const userNameCid = req.params.sponsorCid;
	
    const query = `
      SELECT c.fname AS fullName, c.phone_number AS phoneNumber, c.email, 
