  try {
    const articleUuid = req.params.article_uuid || null; // Provide a default value of null if id is not available
    const query = 'SELECT title, description, date_created, image_path, brief_description, category FROM articles WHERE article_uuid = ? LIMIT 1';

    const [rows, fields] = await db.query(query, [articleUuid]);
    if (rows.length === 0) {
      return res.status(404).json({ error: 'Article not found' });
    }

    const { title, description, date_created, brief_description, category, image_path } = rows[0];
    const articleData = {
      title,
      description,
      date_created,
      brief_description, 
      category,
      image_path
    };
    
