    );  */


    await db.query(
  'UPDATE articles SET title = ?, brief_description = ?, description = ?, category = ?, image_path = ? WHERE article_uuid = ?',
  [title, brief_description, description, category, imagePath || null, articleUuid]
); 



    res.json({ success: true });
  } catch (error) {
    console.error(error);
    res.status(500).send('Server Error');
