      [articleUuid, auuId, title, brief_description, description, imagePath, category, dateCreated, dateLastModified, views]
    );

    res.json({
      articleUuid,
      auuId,
      title,
      brief_description,
      description,
      //imagePath,
      imagePath,   //: 'assets/' + imagePath,
      //imagePath: req.protocol + '://' + req.get('host') + '/assets/' + imagePath,
      category,
