  try {
    let imagePath = ''; // Variable to store the image path

    // Check if a new image is selected
    if (req.file) {
      imagePath = req.file.filename;
    }

    // Update the article data in the database based on the article_uuid
/*         await db.query(
      'UPDATE articles SET title = ?, brief_description = ?, description = ?, category = ?, image_path = ? WHERE article_uuid = ?',
      [title, brief_description, description, category, imagePath, article_uuid]
    );  */


    await db.query(
  'UPDATE articles SET title = ?, brief_description = ?, description = ?, category = ?, image_path = ? WHERE article_uuid = ?',
  [title, brief_description, description, category, imagePath || null, articleUuid]
); 



    res.json({ success: true });
  } catch (error) {
    console.error(error);
    res.status(500).send('Server Error');
  }
});

// Register the router with your Express app
app.use('/api', router);


  // route to get all listings for admin
app.get('/api/dashboard/users', async (req, res) => {
    try {
      const results = await getListingsAllAdminRoute.handler(req, res);
      res.json(results);
    } catch (error) {
      console.error(error);
      res.status(500).send('Server Error');
    }
  });


  
// route to delete a post
app.delete('/api/dashboard/:articleUuid', async (req, res) => {
    try {
      const { articleUuid } = req.params;
      await db.query('DELETE FROM articles WHERE article_uuid=?', [articleUuid]);
      res.json({ message: 'Post deleted successfully' });
    } catch (error) {
      console.error(error);
      res.status(500).send('Server Error');
    }
  });
  

// start the server
app.listen(3000, () => console.log('Server started on port 3000'));

